import React, { useState, useEffect } from 'react';
import { Button, TextField, Typography, Container, List, ListItem, ListItemText } from '@mui/material';

function App() {
  const [gasto, setGasto] = useState({ nome: '', data: '', valor: '' });
  const [gastos, setGastos] = useState([]);
  const [total, setTotal] = useState(0);

  // Carregar os dados do Local Storage ao iniciar
  useEffect(() => {
    const data = localStorage.getItem('gastos');
    if (data) {
      const parsedData = JSON.parse(data);
      setGastos(parsedData);
      calcularTotal(parsedData);
    }
  }, []);

  const handleChange = (e) => {
    const { name, value } = e.target;
    setGasto({ ...gasto, [name]: value });
  };

  const adicionarGasto = () => {
    if (gasto.nome && gasto.data && gasto.valor) {
      const novosGastos = [...gastos, gasto];
      setGastos(novosGastos);
      calcularTotal(novosGastos);
      setGasto({ nome: '', data: '', valor: '' });
      localStorage.setItem('gastos', JSON.stringify(novosGastos));
    }
  };

  const removerGasto = (index) => {
    const novosGastos = gastos.filter((_, i) => i !== index);
    setGastos(novosGastos);
    calcularTotal(novosGastos);
    localStorage.setItem('gastos', JSON.stringify(novosGastos));
  };

  const calcularTotal = (gastos) => {
    const total = gastos.reduce((acc, curr) => acc + parseFloat(curr.valor), 0);
    setTotal(total);
  };

  return (
    <Container maxWidth="sm">
      <Typography variant="h4" gutterBottom>
        Registro de Gastos
      </Typography>
      <TextField
        label="Nome"
        name="nome"
        value={gasto.nome}
        onChange={handleChange}
        fullWidth
        margin="normal"
      />
      <TextField
        name="data"
        value={gasto.data}
        onChange={handleChange}
        fullWidth
        margin="normal"
        type='date'
      />
      <TextField
        label="Valor"
        name="valor"
        value={gasto.valor}
        onChange={handleChange}
        type="number"
        fullWidth
        margin="normal"
      />
      <Button variant="contained" color="primary" onClick={adicionarGasto}>
        Adicionar Gasto
      </Button>

      <Typography variant="h6" style={{ marginTop: '20px' }}>
        Lista de Gastos
      </Typography>
      <List>
        {gastos.map((g, index) => (
          <ListItem key={index} secondaryAction={
            <Button color="secondary" onClick={() => removerGasto(index)}>
              Remover
            </Button>
          }>
            <ListItemText primary={`${g.nome} - ${g.data} - R$${g.valor}`} />
          </ListItem>
        ))}
      </List>

      <Typography variant="h6">
        Total Gasto: R${total.toFixed(2)}
      </Typography>
    </Container>
  );
}

export default App;
